let DXCluster = require('./index.js')

conn = new DXCluster()

let opts = {
  host: 'dxusa.net',
  port: 7300,
  loginPrompt: 'login:',
  call: 'K3AM'
}

conn.connect(opts)
  .then(() => {
    console.log('connected')
  })
  .catch((err) => {
    console.log(err);
  })

conn.on('spot', (spot) => {
  console.log(spot);
})
